package com.xiankezu.sirceo.globals;/** * 一些正则表达式 */import java.util.regex.Matcher;import java.util.regex.Pattern;import android.text.InputFilter;import android.text.Spanned;import android.text.TextUtils;/** * 常用正则验证 *  * @author wangzhenfei * @version 1.0 * @date 2015-04-14 */public class REGX {	/**	 * 手机正则	 */	public static final String REGX_MOBILE_INPUT = "^1$|^1[34578]$|^1[3-8]\\d{0,9}$";		/**中文 5个字 */	public static String  THEMESTR_FORMAT = "^[\u4e00-\u9fa5]{0,5}$";		/**	 * 获得手机号码的正则过滤器	 * @return	 */	public static InputFilter[] getFilters(final String str){		return new InputFilter[] { new InputFilter() {			@Override			public CharSequence filter(CharSequence source, int start, int end,					Spanned dest, int dstart, int dend) {				String content = dest.toString().substring(0, dstart)						+ source						+ dest.toString().substring(dend,								dest.toString().length());				if (TextUtils.isEmpty(content)) {					return source;				} else if (content.matches(str)) {					return source;				} else {					return "";				}			}		}};    	    }	/**	 * 判断号码是否是手机号	 * @param mobiles	 * @return	 */	public static boolean isMobileNum(String mobiles){		boolean flag = false;		if(mobiles.startsWith("+86")){			flag =  true;		}else{			try {				Pattern p = Pattern.compile(REGX_MOBILE_INPUT);				Matcher m = p.matcher(mobiles);				flag = m.matches();			} catch (Exception e) {				flag = false;			}		}						return flag;	}	}